package ingsoftware.service;

import ingsoftware.exception.*;
import ingsoftware.exception.handler.HabitCompletionExceptionHandler;
import ingsoftware.model.DTO.CompletionResultDTO;
import ingsoftware.model.Habit;
import ingsoftware.model.HabitCompletion;
import ingsoftware.model.User;
import ingsoftware.model.builder.HabitCompletionBuilder;
import ingsoftware.repository.HabitCompletionRepository;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import java.time.LocalDate;
import java.time.temporal.ChronoUnit;

@Service
public class HabitCompletionService {

    @Autowired
    private HabitService habitService;
    @Autowired
    private HabitCompletionRepository completionRepository;
    @Autowired
    private GamificationService gamificationService;
    @Autowired
    private UserService userService;
    @Autowired
    private HabitCompletionExceptionHandler exceptionHandler;

    /**
     * Completa un'abitudine per l'utente specificato.
     * Questo metodo utilizza l'exception handler per gestire le eccezioni in modo centralizzato.
     */
    public CompletionResultDTO completeHabit(Long habitId, Long userId) throws BusinessException {
        try {
            exceptionHandler.logInfo("Tentativo di completare l'abitudine con ID {} per l'utente {}", habitId, userId);
            return doCompleteHabit(habitId, userId);
        } catch (HabitNotFoundException e) {
            throw exceptionHandler.handleHabitNotFoundException(e);
        } catch (UserNotFoundException e) {
            throw exceptionHandler.handleUserNotFoundException(e);
        } catch (HabitAlreadyCompletedException e) {
            throw exceptionHandler.handleHabitAlreadyCompletedException(e);
        } catch (Exception e) {
            throw exceptionHandler.handleException(e, "il completamento dell'abitudine");
        }
    }

    /**
     * Implementazione effettiva del completamento dell'abitudine.
     * Questo metodo è privato e viene chiamato da completeHabit che gestisce le eccezioni.
     */
    private CompletionResultDTO doCompleteHabit(Long habitId, Long userId) throws BusinessException {
        LocalDate today = LocalDate.now();

        Habit habit = habitService.findHabitOrThrow(habitId);
        User  user  = userService.findUserOrThrow(userId);

        verifyNotAlreadyCompletedToday(user, habit, today);

        int streak = updateStreak(habit, today);

        HabitCompletion completion = new HabitCompletionBuilder()
                .withUser(user)
                .withHabit(habit)
                .withCompletionDate(today)
                .withStreak(streak)
                .build();

        HabitCompletion savedCompletion = completionRepository.save(completion);

        double xpGained = calculateAndUpdateXP(habit, user);
        exceptionHandler.logDebug("XP guadagnati per abitudine {} completata dall'utente {}: {}", habitId, userId, xpGained);
        int newLevel = calculateAndUpdateLevel(user);

        exceptionHandler.logInfo("Abitudine {} completata con successo per l'utente {}", habitId, userId);
        return new CompletionResultDTO(savedCompletion, xpGained, newLevel);
    }

    /* ---------- METODI DI SUPPORTO ---------- */

    private int calculateAndUpdateLevel(User user) {
        try {
            exceptionHandler.logDebug("Verifica aggiornamento livello per utente {}, livello attuale = {}", 
                    user.getID(), user.getLevel());

            int newLevel = gamificationService.checkUpdateUserLevel(user);
            userService.saveUser(user);

            if (newLevel > user.getLevel()) {
                exceptionHandler.logInfo("Utente {} è salito al livello {}", user.getID(), newLevel);
            }

            return newLevel;
        } catch (Exception e) {
            exceptionHandler.logError("Errore durante l'aggiornamento del livello per l'utente {}: {}", 
                    user.getID(), e.getMessage());
            throw e;
        }
    }

    private double calculateAndUpdateXP(Habit habit, User user) {
        try {
            exceptionHandler.logDebug("Calcolo XP per abitudine {} completata dall'utente {}", 
                    habit.getId(), user.getID());

            double xpGained = gamificationService.calculateHabitXP(habit, user);

            user.addTotalXp(xpGained);
            userService.saveUser(user);

            exceptionHandler.logInfo("Utente {} ha guadagnato {} XP completando l'abitudine {}", 
                    user.getID(), xpGained, habit.getId());

            return xpGained;
        } catch (Exception e) {
            exceptionHandler.logError("Errore durante il calcolo dell'XP per l'abitudine {} e l'utente {}: {}", 
                    habit.getId(), user.getID(), e.getMessage());
            throw e;
        }
    }


    private void verifyNotAlreadyCompletedToday(User user, Habit habit, LocalDate today) {
        boolean alreadyCompleted = completionRepository.existsByUserAndHabitAndCompletionDate(user, habit, today);
        if (alreadyCompleted) {
            exceptionHandler.logInfo("Tentativo di completare l'abitudine {} già completata oggi per l'utente {}", 
                    habit.getId(), user.getID());
            throw new HabitAlreadyCompletedException("Abitudine già completata oggi");
        }
        exceptionHandler.logDebug("Verifica completamento giornaliero superata per abitudine {} e utente {}", 
                habit.getId(), user.getID());
    }

    private int updateStreak(Habit habit, LocalDate today) {
        try {
            LocalDate lastCompleted = habit.getLastCompletedDate();
            int newStreak = habit.getCurrentStreak();

            exceptionHandler.logDebug("Aggiornamento streak per abitudine {}: streak attuale = {}, ultima completata = {}", 
                    habit.getId(), newStreak, lastCompleted);

            if (lastCompleted == null) {
                newStreak = 1;
                exceptionHandler.logInfo("Prima volta che l'abitudine {} viene completata, streak inizializzata a 1", habit.getId());
            } else {
                long daysBetween = ChronoUnit.DAYS.between(lastCompleted, today);
                exceptionHandler.logDebug("Giorni trascorsi dall'ultimo completamento: {}", daysBetween);

                switch (habit.getFrequency()) {
                    case DAILY   -> newStreak = (daysBetween == 1) ? newStreak + 1 : 1;
                    //case WEEKLY  -> newStreak = (daysBetween >= 7 && daysBetween <= 14) ? newStreak + 1 : 1;
                    //case MONTHLY -> newStreak = (daysBetween >= 28 && daysBetween <= 35) ? newStreak + 1 : 1;
                }

                if (daysBetween == 1) {
                    exceptionHandler.logInfo("Streak incrementata per l'abitudine {}: nuovo valore = {}", habit.getId(), newStreak);
                } else {
                    exceptionHandler.logInfo("Streak resettata per l'abitudine {}: nuovo valore = 1", habit.getId());
                }
            }

            habit.setCurrentStreak(newStreak);
            habit.setLastCompletedDate(today);
            habitService.saveHabit(habit);
            return newStreak;
        } catch (Exception e) {
            exceptionHandler.logError("Errore durante l'aggiornamento della streak per l'abitudine {}: {}", 
                    habit.getId(), e.getMessage());
            throw e;
        }
    }
}

