package ingsoftware.exception.handler;

import ingsoftware.exception.*;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.stereotype.Component;

/**
 * Gestore centralizzato delle eccezioni per HabitCompletionService.
 * Fornisce metodi per gestire diversi tipi di eccezioni e standardizzare
 * i messaggi di errore e il logging.
 */
@Component
public class HabitCompletionExceptionHandler {

    private static final Logger logger = LoggerFactory.getLogger(HabitCompletionExceptionHandler.class);

    /**
     * Gestisce un'eccezione generica e la trasforma in BusinessException.
     * 
     * @param e L'eccezione da gestire
     * @param context Informazioni di contesto aggiuntive
     * @return BusinessException con messaggio appropriato
     */
    public BusinessException handleException(Exception e, String context) {
        logger.error("Errore durante {}: {}", context, e.getMessage(), e);
        return new BusinessException("Si è verificato un errore durante " + context + ": " + e.getMessage());
    }

    /**
     * Gestisce l'eccezione HabitNotFoundException.
     * 
     * @param e L'eccezione da gestire
     * @return BusinessException con messaggio appropriato
     */
    public BusinessException handleHabitNotFoundException(HabitNotFoundException e) {
        logger.warn("Abitudine non trovata: {}", e.getMessage());
        return new BusinessException("Abitudine non trovata: " + e.getMessage());
    }

    /**
     * Gestisce l'eccezione UserNotFoundException.
     * 
     * @param e L'eccezione da gestire
     * @return BusinessException con messaggio appropriato
     */
    public BusinessException handleUserNotFoundException(UserNotFoundException e) {
        logger.warn("Utente non trovato: {}", e.getMessage());
        return new BusinessException("Utente non trovato: " + e.getMessage());
    }

    /**
     * Gestisce l'eccezione HabitAlreadyCompletedException.
     * 
     * @param e L'eccezione da gestire
     * @return BusinessException con messaggio appropriato
     */
    public BusinessException handleHabitAlreadyCompletedException(HabitAlreadyCompletedException e) {
        logger.info("Tentativo di completare un'abitudine già completata: {}", e.getMessage());
        logger.info("AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA");
        return new BusinessException(e.getMessage());
    }

    /**
     * Gestisce l'eccezione DuplicateHabitException.
     * 
     * @param e L'eccezione da gestire
     * @return BusinessException con messaggio appropriato
     */
    public BusinessException handleDuplicateHabitException(DuplicateHabitException e) {
        logger.warn("Tentativo di creare un'abitudine duplicata: {}", e.getMessage());
        return new BusinessException("Abitudine duplicata: " + e.getMessage());
    }

    /**
     * Gestisce l'eccezione EquipmentNotFoundException.
     * 
     * @param e L'eccezione da gestire
     * @return BusinessException con messaggio appropriato
     */
    public BusinessException handleEquipmentNotFoundException(EquipmentNotFoundException e) {
        logger.warn("Equipaggiamento non trovato: {}", e.getMessage());
        return new BusinessException("Equipaggiamento non trovato: " + e.getMessage());
    }

    /**
     * Metodo di utilità per loggare informazioni di debug.
     * 
     * @param message Il messaggio da loggare
     * @param args Argomenti opzionali per il messaggio
     */
    public void logDebug(String message, Object... args) {
        logger.debug(message, args);
    }

    /**
     * Metodo di utilità per loggare informazioni.
     * 
     * @param message Il messaggio da loggare
     * @param args Argomenti opzionali per il messaggio
     */
    public void logInfo(String message, Object... args) {
        logger.info(message, args);
    }

    /**
     * Metodo di utilità per loggare errori.
     * 
     * @param message Il messaggio da loggare
     * @param args Argomenti opzionali per il messaggio
     */
    public void logError(String message, Object... args) {
        logger.error(message, args);
    }
}

