+package ingsoftware.controller.listener;
+
+import ingsoftware.service.events.HabitExceptionEvent;
+import javafx.application.Platform;
+import javafx.scene.control.Alert;
+import org.slf4j.Logger;
+import org.slf4j.LoggerFactory;
+import org.springframework.context.event.EventListener;
+import org.springframework.stereotype.Component;
+
+/**
+ * Listener per eventi di eccezione che devono essere mostrati nell'UI
+ */
+@Component
+public class ExceptionEventListener {
+    
+    private static final Logger logger = LoggerFactory.getLogger(ExceptionEventListener.class);
+    
+    @EventListener
+    public void handleHabitExceptionEvent(HabitExceptionEvent event) {
+        logger.debug("Ricevuto evento di eccezione: {} per abitudine {} e utente {}", 
+                    event.getExceptionType(), event.getHabitId(), event.getUserId());
+        
+        // Esegui sulla UI thread di JavaFX
+        Platform.runLater(() -> {
+            showExceptionDialog(event);
+        });
+    }
+    
+    private void showExceptionDialog(HabitExceptionEvent event) {
+        Alert.AlertType alertType = switch (event.getSeverity()) {
+            case INFO -> Alert.AlertType.INFORMATION;
+            case WARNING -> Alert.AlertType.WARNING;
+            case ERROR -> Alert.AlertType.ERROR;
+        };
+        
+        Alert alert = new Alert(alertType);
+        alert.setTitle("Notifica");
+        alert.setHeaderText(getHeaderForExceptionType(event.getExceptionType()));
+        alert.setContentText(event.getMessage());
+        
+        // Personalizza il messaggio in base al tipo di eccezione
+        if ("HabitAlreadyCompleted".equals(event.getExceptionType())) {
+            alert.setHeaderText("Abitudine già completata");
+            alert.setContentText("Hai già completato questa abitudine oggi! 🎉");
+        }
+        
+        alert.showAndWait();
+    }
+    
+    private String getHeaderForExceptionType(String exceptionType) {
+        return switch (exceptionType) {
+            case "HabitAlreadyCompleted" -> "Abitudine già completata";
+            case "HabitNotFound" -> "Abitudine non trovata";
+            case "UserNotFound" -> "Utente non trovato";
+            case "DuplicateHabit" -> "Abitudine duplicata";
+            default -> "Notifica";
+        };
+    }
+}

